export const isArray = (v) => {
    return Array.isArray(v);
};
export const hasMinSize = (v, minSize) => {
    return v.length >= minSize;
};
export const hasMaxSize = (v, maxSize) => {
    return v.length <= maxSize;
};
export const hasSize = (v, size) => {
    return v.length === size;
};
export const isArrayOfType = (v, type) => {
    return v.every((x) => typeof x === type);
};
