export const isBoolean = (v) => {
    return typeof v === "boolean";
};
export const isTrue = (v) => {
    return typeof v === "boolean" && v;
};
export const isFalse = (v) => {
    return typeof v === "boolean" && !v;
};
export const isTruthy = (v) => {
    return typeof v === "boolean" && !!v;
};
export const isFalsy = (v) => {
    return typeof v === "boolean" && !!!v;
};
