export const isNumber = (v) => {
    return ((v !== Infinity &&
        v !== -Infinity &&
        typeof v === "number" &&
        !Number.isNaN(v)) ||
        (typeof v === "string" && v.trim() !== "" && !Number.isNaN(+v)));
};
export const isInteger = (v) => {
    return Number.isInteger(+v);
};
export const isGreaterThan = (v, valueToCompareWith) => {
    return +v > +valueToCompareWith;
};
export const isGreaterOrEqualTo = (v, valueToCompareWith) => {
    return +v >= +valueToCompareWith;
};
export const isLowerThan = (v, valueToCompareWith) => {
    return +v < +valueToCompareWith;
};
export const isLowerOrEqualTo = (v, valueToCompareWith) => {
    return +v <= +valueToCompareWith;
};
export const isEqualTo = (v, valueToCompareWith) => {
    return +v == +valueToCompareWith;
};
export const isInRange = (v, min, max) => {
    return +v >= +min && +v <= +max;
};
